// logout
cy.get('span.fa.fa-power-off').click();
// should be redirected to home page
cy.location('pathname')
    .should('eq', '/')
    // and localStorage should be clean
    .and(() => expect(localStorage.getItem('rememberMe')).to.eq(null));
